import '../App.css';
import Simulation from './Simulation';
import React, { useState } from 'react';
// import RatDataComponent from './RatDataComponent';
import Settings from './Settings';
//import axios from 'axios';

function App() {
  const [showMenu, setShowMenu] = useState(1);
  const [startSim, setStartSim] = useState(false);

  function handleClick(){
    setShowMenu(0);
  }

  function startSimulation(){
    setStartSim(true)
  }

  return (
    <div className="App">
      {showMenu && <header className="App-header">
        <p id="title">
          RAT SIMULATOR
        </p>
        <div className="entry-container">
          <p>This is a rat simulator. Learn more in <a>About</a>.</p>
          <div className = "submit" onClick = {handleClick}>Simulate</div>
        </div>
      </header> }
      {/* <RatDataComponent/> */}


      {!showMenu && 
        <div>
          <Simulation n={10} startSim={startSim}/>
          <Settings startSimulation = {() => startSimulation()}/>
        </div>
      }

    </div>
  );
}

export default App;
