//import Box2 from './Box2';
import React, { useState, useEffect } from 'react';
import '../index.css';

const mice = [];


function Simulation(props){

    const [miceItems, setMiceItems] = useState([]);
    const [startSim, setStartSim] = useState(false);

    function updateMiceDisplay(){
        setMiceItems(mice.map(mouse => 
            <div key={mouse.id}>
              <p className='mouse' style={{left: mouse.id*100}}>Mouse</p>

            </div>
        ));
    }

    useEffect(() => {
        for(let i = 0; i < 1; i++){
            mice[i] = {
                id: i, 
                frontal: 50,
                occipital: 50,
                hypothalumus: 50,
                parietal: 50,
                cerebellum: 50,
                posX: 50,
                posY: 50
            };
        }
        updateMiceDisplay();
    }, []);

    useEffect(() => {
        let data = {num_rats: 20, num_food: 10, total_iter: 20, moves_per_iter: 50, map_radius: 50};

        fetch("/anze/", {
            method: "POST",
            headers: {
            "Content-Type": "application/json",
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer", // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
            body: JSON.stringify(data) // body data type must match "Content-Type" header
        })
            //.then((res) => res.json())
            .then((result) => {
              console.log(result)
            })

        for(let i = 0; i < 1; i++){
            mice[i] = {
                id: i, 
                frontal: 50,
                occipital: 50,
                hypothalumus: 50,
                parietal: 50,
                cerebellum: 50,
                posX: 50,
                posY: 50
            };
        }
        updateMiceDisplay();
    }, [startSim]);

    const interval = setInterval(() => {
        console.log('FRAME');
        //updateMiceDisplay();
    }, 1000); //each frame


    return (
       <>header
       {miceItems}
       </>
    );
}

export default Simulation;
