//import Box2 from './Box2';
import React, { useState, useEffect } from 'react';
import '../index.css';

const mice = [];


function Simulation(props){

    const [miceItems, setMiceItems] = useState([]);

    const [totalSim, setTotalSim] = useState({});

    function updateMiceDisplay(){
        setMiceItems(mice.map(mouse => 
            <div key={mouse.id}>
              <p className={'mouse mouse_'+mouse.id} style={{left: mouse.posX, top: mouse.posY}}>Mouse</p>

            </div>
        ));
    }

    useEffect(() => {
        for(let i = 0; i < 1; i++){
            mice[i] = {
                id: i, 
                frontal: 50,
                occipital: 50,
                hypothalumus: 50,
                parietal: 50,
                cerebellum: 50,
                posX: 50,
                posY: 50
            };
        }
        updateMiceDisplay();
    }, []);

    useEffect(() => {
        if(!props.startSim) return;

        let data = {num_food: 10, total_iter: 20, moves_per_iter: 50, map_radius: 50, michael_nums: [0]};
        let fetchResult = {}
        fetch("http://localhost:8000/anze/", {
            method: "POST",
            headers: {
            "Content-Type": "application/json",
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer", // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
            body: JSON.stringify(data) // body data type must match "Content-Type" header
        })
            .then((res) => res.json())
            .then((result) => {
                fetchResult = result;
              console.log(result);
            })

        for(let i = 0; i < props.num; i++){
            mice[i] = {
                id: i, 
                posX: 50,//fetchResult[i]
                posY: 50//fetchResult[i]
            };
        }
        updateMiceDisplay();
    }, [props.startSim]);

    const interval = setInterval(() => {
        console.log('FRAME');
        //updateMiceDisplay();
    }, 1000); //each frame


    return (
       <>header
       {miceItems}
       </>
    );
}

export default Simulation;
